extensions.client:
var checkAction = {
    'name': 'Check',
    'fn': async function (data) {
        var url = data.url;
        if (url) {
            var surl;
            if (url.startsWith("http://")) {
                surl = url.substring(7);
            } else if (url.startsWith("https://")) {
                surl = url.substring(8);
            } else {
                surl = url;
            }

            if (surl.startsWith("9gag.com/gag/")) {
                var id = surl.substring(13, 21);
                data.thumbnail = { url: "https://img-9gag-fun.9cache.com/photo/" + id + "_700b.jpg" };
            }
        }
        return Promise.resolve(data);
    }
};
this._crudDialogActions.push(checkAction);
